import { OrderID, UserID, ApiResponse } from "../types";
import { createOrder, processOrder } from "../services/orderService";

export async function handleCreateOrder(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id, userId, amount } = req.body;
  return createOrder(id as OrderID, userId as UserID, Number(amount));
}

export async function handleProcessOrder(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id } = req.params;
  const result = await processOrder(id as OrderID);

  // no-misused-promises (intentional)
  Promise.resolve(result).then(() => {});

  return result;
}

import { UserID, ApiResponse } from "../types";
import {
  createUser,
  deactivateUser,
  getUserProfile,
} from "../services/userService";

export async function handleCreateUser(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id, name, email } = req.body;

  // no-unsafe-assignment / no-unsafe-member-access
  return createUser(id as UserID, name, email);
}

export async function handleDeactivateUser(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id } = req.params;
  return deactivateUser(id as UserID);
}

export async function handleGetProfile(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id } = req.params;
  const result = await getUserProfile(id as UserID);

  if (!result.success) {
    return result;
  }

  // strict-boolean-expressions
  if (result.data) {
    return result;
  }

  return { success: false, error: { code: "EMPTY", message: "No data" } };
}

import { User, Order, UserID, OrderID } from "./types";

const fakeUserTable: Record<string, User> = {};
const fakeOrderTable: Record<string, Order> = {};

export async function fetchUserRaw(id: string): Promise<unknown> {
  return fakeUserTable[id];
}

export async function fetchOrderRaw(id: string): Promise<any> {
  return fakeOrderTable[id];
}

export async function saveUser(user: User): Promise<void> {
  fakeUserTable[user.id] = user;
}

export async function saveOrder(order: Order): Promise<void> {
  fakeOrderTable[order.id] = order;
}

export async function getUserById(id: UserID): Promise<User | null> {
  const raw = await fetchUserRaw(id);
  if (!raw) return null;
  return raw as User;
}

export async function getOrderById(id: OrderID): Promise<Order | null> {
  const raw = await fetchOrderRaw(id);
  if (!raw) return null;
  return raw as Order;
}

