import fs from "fs";
import path from "path";
import { ESLint } from "eslint";
import { MemProfiler } from "./mem-profiler.ts";
import { ConfigConverter } from "./config-converter.ts";

const projectRoot = process.cwd();

async function main() {
  console.log("ðŸš€ Starting ESLint Baseline\n");

  // 1. Load legacy config
  const legacyConfigPath = path.join(projectRoot, ".eslintrc.json");

  if (!fs.existsSync(legacyConfigPath)) {
    console.error("âŒ No .eslintrc.json found");
    process.exit(1);
  }

  const legacyConfig = ConfigConverter.loadLegacyConfig(legacyConfigPath);
  console.log("âœ“ Legacy config loaded");
  console.log(`  - Parser: ${legacyConfig.parser}`);
  console.log(`  - Rules: ${Object.keys(legacyConfig.rules || {}).length}\n`);

  // 2. Convert to flat config
  const { source } = ConfigConverter.convert(legacyConfig);
  const flatConfigPath = path.join(projectRoot, "eslint.config.mjs");
  ConfigConverter.writeFlatConfig(source, flatConfigPath);
  console.log("âœ“ Flat config written\n");

  // 3. Start profiling
  const profiler = new MemProfiler();
  profiler.start(100);
  profiler.sample("before-eslint");

  // 4. Initialize ESLint
  const eslint = new ESLint({
    cwd: projectRoot,
    overrideConfigFile: flatConfigPath,
    cache: false,
  });

  profiler.sample("after-init");
  console.log("âœ“ ESLint initialized\n");

  // 5. Run ESLint
  console.log("ðŸ” Linting src/**/*.ts...\n");

  let results;
  try {
    results = await eslint.lintFiles(["src/**/*.ts"]);
    profiler.sample("after-lint");
  } catch (error: any) {
    profiler.sample("error");
    profiler.stop();
    profiler.dump("memory-timeline-error.json");

    console.error("âŒ ESLint failed:");
    console.error(error.message);

    if (error.message.includes("parserServices")) {
      console.error("\nðŸ’¡ Parser issue detected. Check:");
      console.error("   - TypeScript ESLint version (need v8+ for ESLint v9)");
      console.error("   - tsconfig.json exists and includes src files");
    }

    process.exit(1);
  }

  // 6. Save results
  fs.writeFileSync("eslint-results.json", JSON.stringify(results, null, 2));
  profiler.stop();
  profiler.dump("memory-timeline.json");

  // 7. Summary
  const totalIssues = results.reduce(
    (s, r) => s + r.errorCount + r.warningCount,
    0,
  );
  const totalErrors = results.reduce((s, r) => s + r.errorCount, 0);
  const totalWarnings = results.reduce((s, r) => s + r.warningCount, 0);

  console.log("âœ… Complete!\n");
  console.log("ðŸ“Š Results:");
  console.log(`  - Files: ${results.length}`);
  console.log(`  - Issues: ${totalIssues}`);
  console.log(`    â€¢ Errors: ${totalErrors}`);
  console.log(`    â€¢ Warnings: ${totalWarnings}\n`);

  console.log("ðŸ“ Output:");
  console.log("  - eslint-results.json");
  console.log("  - memory-timeline.json");
  console.log("  - eslint.config.mjs");

  // Top issues
  if (totalIssues > 0) {
    const issuesByRule = new Map<string, number>();
    results.forEach((r) => {
      r.messages.forEach((msg) => {
        const rule = msg.ruleId || "unknown";
        issuesByRule.set(rule, (issuesByRule.get(rule) || 0) + 1);
      });
    });

    const top5 = Array.from(issuesByRule.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 5);

    console.log("\nðŸ” Top 5 Issues:");
    top5.forEach(([rule, count], i) => {
      console.log(`  ${i + 1}. ${rule}: ${count}x`);
    });
  }
}

main().catch((err) => {
  console.error("\nðŸ’¥ Error:", err.message);
  process.exit(1);
});

// src/tools/config-converter.ts

import fs from "fs";

interface LegacyConfig {
  extends?: string[];
  parser?: string;
  parserOptions?: Record<string, any>;
  plugins?: string[];
  rules?: Record<string, any>;
}

export class ConfigConverter {
  static convert(legacyConfig: LegacyConfig) {
    const isTypeScript = this.usesTypeScript(legacyConfig);

    if (!isTypeScript) {
      throw new Error("Only TypeScript projects are supported in this PoC");
    }

    const source = this.generateTypeScriptConfig(legacyConfig);
    return { source };
  }

  private static usesTypeScript(config: LegacyConfig): boolean {
    return !!(
      config.parser?.includes("@typescript-eslint/parser") ||
      config.plugins?.includes("@typescript-eslint") ||
      config.extends?.some((e) => e.includes("@typescript-eslint"))
    );
  }

  private static generateTypeScriptConfig(config: LegacyConfig): string {
    // Build rules
    const rulesets: string[] = [];
    const extendsArray = config.extends || [];

    if (extendsArray.includes("plugin:@typescript-eslint/recommended")) {
      rulesets.push("...tsPlugin.configs.recommended.rules");
    }

    if (
      extendsArray.some(
        (e) =>
          e.includes("recommended-requiring-type-checking") ||
          e.includes("recommended-type-checked"),
      )
    ) {
      rulesets.push(
        "...tsPlugin.configs['recommended-requiring-type-checking'].rules",
      );
    }

    // User custom rules
    const userRules = Object.entries(config.rules || {}).map(
      ([key, value]) => `"${key}": ${JSON.stringify(value)}`,
    );

    const allRules = [...rulesets, ...userRules].join(",\n      ");

    // Parser options
    const parserOptions = config.parserOptions || {};
    const project = parserOptions.project || "./tsconfig.json";

    return `import tsParser from "@typescript-eslint/parser";
import tsPlugin from "@typescript-eslint/eslint-plugin";
import path from "node:path";
import { fileURLToPath } from "node:url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

export default [
  {
    ignores: ["eslint.config.mjs", "dist/**", "node_modules/**"],
  },
  {
    files: ["**/*.ts", "**/*.tsx"],
    languageOptions: {
      parser: tsParser,
      parserOptions: {
        project: ${JSON.stringify(project)},
        tsconfigRootDir: __dirname,
      },
      ecmaVersion: "latest",
      sourceType: "module",
    },
    plugins: {
      "@typescript-eslint": tsPlugin,
    },
    rules: {
      ${allRules}
    },
  },
];`;
  }

  static loadLegacyConfig(configPath: string): LegacyConfig {
    const content = fs.readFileSync(configPath, "utf8");
    return JSON.parse(content);
  }

  static writeFlatConfig(source: string, outputPath: string): void {
    fs.writeFileSync(outputPath, source, "utf8");
  }
}

import fs from "fs";

function mb(n: number) {
  return Number((n / 1024 / 1024).toFixed(2));
}

export class MemProfiler {
  private timeline: any[] = [];
  private timer: NodeJS.Timer | null = null;

  start(intervalMs = 100) {
    this.timer = setInterval(() => {
      this.sample("interval");
    }, intervalMs);
  }

  stop() {
    if (this.timer) clearInterval(this.timer);
    this.timer = null;
  }

  sample(label: string) {
    const m = process.memoryUsage();

    this.timeline.push({
      ts: Date.now(),
      label,
      pid: process.pid,

      // raw bytes (for exact thresholds)
      rss: m.rss,
      heapTotal: m.heapTotal,
      heapUsed: m.heapUsed,
      external: m.external,
      arrayBuffers: (m as any).arrayBuffers,

      // human readable (for debugging / graphs)
      rssMB: mb(m.rss),
      heapTotalMB: mb(m.heapTotal),
      heapUsedMB: mb(m.heapUsed),
      externalMB: mb(m.external),
      arrayBuffersMB: mb((m as any).arrayBuffers),
    });
  }

  dump(file = "memory-timeline.json") {
    fs.writeFileSync(file, JSON.stringify(this.timeline, null, 2));
  }
}

import fs from "fs";
import path from "path";
import glob from "glob";
import ts from "typescript";

const projectRoot = process.cwd();
const files = glob.sync("src/**/*.ts", { absolute: true });
const outDir = path.join(projectRoot, "ast-dump");

fs.mkdirSync(outDir, { recursive: true });

// Custom serializer to handle TypeScript AST nodes
function serializeNode(node, sourceFile) {
  const obj = {
    kind: ts.SyntaxKind[node.kind],
    kindNum: node.kind,
    pos: node.pos,
    end: node.end,
  };

  // Add text for identifier-like nodes
  if (node.kind === ts.SyntaxKind.Identifier) {
    obj.text = node.text;
  }

  // Add flags if present
  if (node.flags) {
    obj.flags = node.flags;
  }

  // Add modifiers if present
  if (node.modifiers) {
    obj.modifiers = node.modifiers.map((m) => ts.SyntaxKind[m.kind]);
  }

  // Recursively serialize children
  const children = [];
  ts.forEachChild(node, (child) => {
    children.push(serializeNode(child, sourceFile));
  });

  if (children.length > 0) {
    obj.children = children;
  }

  return obj;
}

for (const filePath of files) {
  const code = fs.readFileSync(filePath, "utf8");
  const sourceFile = ts.createSourceFile(
    filePath,
    code,
    ts.ScriptTarget.Latest,
    true,
    ts.ScriptKind.TS,
  );

  const ast = serializeNode(sourceFile, sourceFile);

  const relative = path
    .relative(projectRoot, filePath)
    .replace(/[\/\\]/g, "__");
  const outFile = path.join(outDir, relative + ".ast.json");
  fs.writeFileSync(outFile, JSON.stringify(ast, null, 2));
  console.log(`AST written: ${outFile}`);
}

console.log("All ASTs dumped to ast-dump/");

export type UserID = string & { readonly brand: unique symbol };
export type OrderID = string & { readonly brand: unique symbol };

export interface User {
  id: UserID;
  name: string;
  email: string | null;
  isActive: boolean;
  metadata?: Record<string, unknown>;
}

export interface Order {
  id: OrderID;
  userId: UserID;
  amount: number;
  status: "pending" | "paid" | "cancelled";
  createdAt: Date;
  notes?: string;
}

export interface ApiResponse<T> {
  success: boolean;
  data?: T;
  error?: {
    code: string;
    message: string;
  };
}

export type Maybe<T> = T | null | undefined;

export function assertNever(x: never): never {
  throw new Error("Unexpected object: " + x);
}

import { Maybe } from "./types";

export function isNonEmptyString(value: Maybe<string>): boolean {
  return !!value && value.trim().length > 0;
}

export async function delay(ms: number): Promise<void> {
  return new Promise((resolve) => setTimeout(resolve, ms));
}

export function fireAndForget(p: Promise<unknown>): void {
  // Intentionally not awaited
  p.then(() => {}).catch(() => {});
}

export function riskyBoolean(value: unknown): boolean {
  // strict-boolean-expressions will complain
  if (value) {
    return true;
  }
  return false;
}
