// src/tools/worker.ts - Isolated ESLint worker process

import { ESLint } from "eslint";
import { LintTask, LintResult, WorkerError, MemorySample } from "./types.ts";
import {
  getTestConfig,
  shouldSimulateFailure,
  executeFailure,
} from "./test-scenarios.ts";

let workerId = -1;
let peakRSS = 0;

// Load test config once
const testConfig = getTestConfig();

function sendMessage(msg: LintResult | WorkerError | MemorySample) {
  if (process.send) {
    process.send(msg);
  }
}

function sampleMemory(): number {
  const mem = process.memoryUsage();
  if (mem.rss > peakRSS) {
    peakRSS = mem.rss;
  }

  sendMessage({
    type: "memory",
    workerId,
    rss: mem.rss,
    heapUsed: mem.heapUsed,
    timestamp: Date.now(),
  });

  return mem.rss;
}

async function runLint(task: LintTask): Promise<void> {
  workerId = task.workerId;
  const startTime = Date.now();

  console.log(
    `[Worker ${workerId}] Starting lint of ${task.files.length} files`,
  );

  if (testConfig.scenario !== "none") {
    console.log(`[Worker ${workerId}] ğŸ§ª Test mode: ${testConfig.scenario}`);
  }

  // Start memory sampling
  const memInterval = setInterval(() => sampleMemory(), 200);

  try {
    // ============ TEST HOOK: Pre-lint failures ============
    // Check each file for simulated failures BEFORE linting
    for (const file of task.files) {
      const { shouldFail, failureType } = shouldSimulateFailure(
        file,
        testConfig,
      );
      if (shouldFail && failureType) {
        clearInterval(memInterval);
        executeFailure(failureType, file);
        // If executeFailure didn't exit/throw (e.g., slow), continue
      }
    }
    // ======================================================

    // Initialize ESLint with the pre-generated flat config
    const eslint = new ESLint({
      cwd: process.cwd(),
      overrideConfigFile: task.configPath,
      cache: false,
    });

    sampleMemory();

    // Lint the assigned files
    const results = await eslint.lintFiles(task.files);

    sampleMemory();
    clearInterval(memInterval);

    const duration = Date.now() - startTime;

    console.log(
      `[Worker ${workerId}] Completed in ${duration}ms, peak RSS: ${(peakRSS / 1024 / 1024).toFixed(1)}MB`,
    );

    // Send results back to master
    sendMessage({
      type: "result",
      workerId,
      results,
      peakRSS,
      duration,
    });
  } catch (error: unknown) {
    clearInterval(memInterval);

    const errMsg = error instanceof Error ? error.message : String(error);
    console.error(`[Worker ${workerId}] Error:`, errMsg);

    // Classify the error
    let errorType: "parse_error" | "rule_crash" | "unknown" = "unknown";
    let file: string | undefined;

    if (errMsg.includes("Parsing error") || errMsg.includes("parserServices")) {
      errorType = "parse_error";
      // Try to extract file from error
      const match = errMsg.match(/in\s+([^\s:]+)/);
      if (match) file = match[1];
    } else if (errMsg.includes("Rule ") || errMsg.includes("rule '")) {
      errorType = "rule_crash";
    }

    sendMessage({
      type: "error",
      workerId,
      errorType,
      message: errMsg,
      file,
    });
  }

  // Exit cleanly
  process.exit(0);
}

// Listen for task from master
process.on("message", (msg: LintTask) => {
  if (msg.type === "lint") {
    runLint(msg).catch((err) => {
      console.error(`[Worker ${workerId}] Fatal:`, err);
      process.exit(1);
    });
  }
});

// Handle uncaught errors
process.on("uncaughtException", (err) => {
  console.error(`[Worker ${workerId}] Uncaught:`, err.message);
  sendMessage({
    type: "error",
    workerId,
    errorType: "unknown",
    message: err.message,
  });
  process.exit(1);
});

console.log(`[Worker] Process started, PID: ${process.pid}`);

import fs from "fs";
import path from "path";
import glob from "glob";
import ts from "typescript";

const projectRoot = process.cwd();
const files = glob.sync("src/**/*.ts", { absolute: true });
const outDir = path.join(projectRoot, "ast-dump");

fs.mkdirSync(outDir, { recursive: true });

// Custom serializer to handle TypeScript AST nodes
function serializeNode(node, sourceFile) {
  const obj = {
    kind: ts.SyntaxKind[node.kind],
    kindNum: node.kind,
    pos: node.pos,
    end: node.end,
  };

  // Add text for identifier-like nodes
  if (node.kind === ts.SyntaxKind.Identifier) {
    obj.text = node.text;
  }

  // Add flags if present
  if (node.flags) {
    obj.flags = node.flags;
  }

  // Add modifiers if present
  if (node.modifiers) {
    obj.modifiers = node.modifiers.map((m) => ts.SyntaxKind[m.kind]);
  }

  // Recursively serialize children
  const children = [];
  ts.forEachChild(node, (child) => {
    children.push(serializeNode(child, sourceFile));
  });

  if (children.length > 0) {
    obj.children = children;
  }

  return obj;
}

for (const filePath of files) {
  const code = fs.readFileSync(filePath, "utf8");
  const sourceFile = ts.createSourceFile(
    filePath,
    code,
    ts.ScriptTarget.Latest,
    true,
    ts.ScriptKind.TS,
  );

  const ast = serializeNode(sourceFile, sourceFile);

  const relative = path
    .relative(projectRoot, filePath)
    .replace(/[\/\\]/g, "__");
  const outFile = path.join(outDir, relative + ".ast.json");
  fs.writeFileSync(outFile, JSON.stringify(ast, null, 2));
  console.log(`AST written: ${outFile}`);
}

console.log("All ASTs dumped to ast-dump/");

// src/tools/test-scenarios.ts - Test utilities for simulating failures

export type TestScenario =
  | "none" // Normal operation
  | "oom-single" // OOM on one file (recoverable)
  | "oom-persistent" // OOM that keeps failing (tests max retries)
  | "parse-error" // Syntax error in file
  | "rule-crash" // ESLint rule throws
  | "random-oom" // Random OOM (50% chance)
  | "slow-worker" // Worker takes very long (timeout test)
  | "all"; // Run multiple scenarios

export interface TestConfig {
  scenario: TestScenario;
  targetFile?: string; // File pattern to trigger on
  oomRetryCount?: number; // How many times to fail before succeeding (for oom-single)
}

// Track OOM attempts per file (for recoverable OOM testing)
const oomAttempts = new Map<string, number>();

/**
 * Check if we should simulate a failure for this file
 */
export function shouldSimulateFailure(
  file: string,
  config: TestConfig,
): { shouldFail: boolean; failureType?: string } {
  const targetPattern = config.targetFile ?? "orderService";
  const isTargetFile = file.includes(targetPattern);

  switch (config.scenario) {
    case "none":
      return { shouldFail: false };

    case "oom-single": {
      // Fail first N times, then succeed (tests retry logic)
      if (!isTargetFile) return { shouldFail: false };

      const attempts = oomAttempts.get(file) ?? 0;
      const maxFails = config.oomRetryCount ?? 1;

      if (attempts < maxFails) {
        oomAttempts.set(file, attempts + 1);
        return { shouldFail: true, failureType: "oom" };
      }
      return { shouldFail: false };
    }

    case "oom-persistent": {
      // Always fail on target file (tests permanent failure)
      if (isTargetFile) {
        return { shouldFail: true, failureType: "oom" };
      }
      return { shouldFail: false };
    }

    case "parse-error": {
      if (isTargetFile) {
        return { shouldFail: true, failureType: "parse-error" };
      }
      return { shouldFail: false };
    }

    case "rule-crash": {
      if (isTargetFile) {
        return { shouldFail: true, failureType: "rule-crash" };
      }
      return { shouldFail: false };
    }

    case "random-oom": {
      // 30% chance of OOM on any file
      if (Math.random() < 0.3) {
        return { shouldFail: true, failureType: "oom" };
      }
      return { shouldFail: false };
    }

    case "slow-worker": {
      if (isTargetFile) {
        return { shouldFail: true, failureType: "slow" };
      }
      return { shouldFail: false };
    }

    case "all": {
      // Cycle through different failures based on file
      if (file.includes("orderService")) {
        return { shouldFail: true, failureType: "oom" };
      }
      if (file.includes("userService")) {
        return { shouldFail: true, failureType: "parse-error" };
      }
      if (file.includes("db")) {
        return { shouldFail: true, failureType: "rule-crash" };
      }
      return { shouldFail: false };
    }

    default:
      return { shouldFail: false };
  }
}

/**
 * Execute the simulated failure
 */
export function executeFailure(
  failureType: string,
  file: string,
): never | void {
  switch (failureType) {
    case "oom":
      console.log(`[TEST] ğŸ’¥ Simulating OOM on ${file}`);
      process.exit(137);

    case "parse-error":
      console.log(`[TEST] ğŸ’¥ Simulating parse error on ${file}`);
      throw new Error(
        `Parsing error: Unexpected token in ${file}. Expected ';' but found 'const'.`,
      );

    case "rule-crash":
      console.log(`[TEST] ğŸ’¥ Simulating rule crash on ${file}`);
      throw new Error(
        `Rule '@typescript-eslint/no-unsafe-assignment' threw: Cannot read property 'type' of undefined`,
      );

    case "slow":
      console.log(`[TEST] ğŸ¢ Simulating slow processing on ${file}`);
      // Block for 30 seconds
      const start = Date.now();
      while (Date.now() - start < 30000) {
        // Busy wait
      }
      break;

    default:
      console.log(`[TEST] Unknown failure type: ${failureType}`);
  }
}

/**
 * Parse test scenario from command line or env
 */
export function getTestConfig(): TestConfig {
  // Check command line args
  const args = process.argv.slice(2);

  for (const arg of args) {
    if (arg.startsWith("--test=")) {
      const scenario = arg.replace("--test=", "") as TestScenario;
      return { scenario };
    }
    if (arg.startsWith("--test-file=")) {
      const targetFile = arg.replace("--test-file=", "");
      return { scenario: "oom-single", targetFile };
    }
  }

  // Check environment variable
  const envScenario = process.env.TEST_SCENARIO as TestScenario;
  if (envScenario) {
    return {
      scenario: envScenario,
      targetFile: process.env.TEST_TARGET_FILE,
      oomRetryCount: process.env.TEST_OOM_RETRIES
        ? parseInt(process.env.TEST_OOM_RETRIES)
        : undefined,
    };
  }

  return { scenario: "none" };
}

