import { Order, OrderID, UserID, ApiResponse } from "../types";
import { getOrderById, saveOrder, getUserById } from "../db";
import { delay } from "../utils";

export async function createOrder(
  id: OrderID,
  userId: UserID,
  amount: number,
): Promise<ApiResponse<Order>> {
  const user = await getUserById(userId);
  if (!user) {
    return {
      success: false,
      error: { code: "USER_NOT_FOUND", message: "User does not exist" },
    };
  }

  const order: Order = {
    id,
    userId,
    amount,
    status: "pending",
    createdAt: new Date(),
  };

  await saveOrder(order);
  return { success: true, data: order };
}

export async function processOrder(id: OrderID): Promise<ApiResponse<Order>> {
  const order = await getOrderById(id);

  if (!order) {
    return {
      success: false,
      error: { code: "ORDER_NOT_FOUND", message: "Order not found" },
    };
  }

  if (order.amount <= 0) {
    return {
      success: false,
      error: { code: "INVALID_AMOUNT", message: "Amount must be positive" },
    };
  }

  if (order.status === "pending") {
    order.status = "paid";
  } else if (order.status === "paid") {
    return {
      success: false,
      error: { code: "ALREADY_PAID", message: "Order already paid" },
    };
  } else if (order.status === "cancelled") {
    return {
      success: false,
      error: { code: "CANCELLED", message: "Order cancelled" },
    };
  } else {
    // Exhaustiveness check
    return {
      success: false,
      error: { code: "UNKNOWN", message: "Unknown status" },
    };
  }

  // no-misused-promises
  setTimeout(async () => {
    await delay(100);
  }, 0);

  await saveOrder(order);
  return { success: true, data: order };
}

import { UserID, ApiResponse } from "../types";
import {
  createUser,
  deactivateUser,
  getUserProfile,
} from "../services/userService";

export async function handleCreateUser(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id, name, email } = req.body;

  // no-unsafe-assignment / no-unsafe-member-access
  return createUser(id as UserID, name, email);
}

export async function handleDeactivateUser(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id } = req.params;
  return deactivateUser(id as UserID);
}

export async function handleGetProfile(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id } = req.params;
  const result = await getUserProfile(id as UserID);

  if (!result.success) {
    return result;
  }

  // strict-boolean-expressions
  if (result.data) {
    return result;
  }

  return { success: false, error: { code: "EMPTY", message: "No data" } };
}

import { OrderID, UserID, ApiResponse } from "../types";
import { createOrder, processOrder } from "../services/orderService";

export async function handleCreateOrder(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id, userId, amount } = req.body;
  return createOrder(id as OrderID, userId as UserID, Number(amount));
}

export async function handleProcessOrder(
  req: any,
): Promise<ApiResponse<unknown>> {
  const { id } = req.params;
  const result = await processOrder(id as OrderID);

  // no-misused-promises (intentional)
  Promise.resolve(result).then(() => {});

  return result;
}
